#!/bin/bash\n# 03_parametri_posizionali.sh\n\necho \"Questo script dimostra l'uso dei parametri posizionali.\"\n\necho \"Nome dello script: $0\"\necho \"Numero totale di argomenti passati: $#\"\n\necho \"Primo argomento: $1\"\necho \"Secondo argomento: $2\"\necho \"Terzo argomento: $3\"\n\necho \"Tutti gli argomenti (come stringa singola, v1 - \$*): $*\"\necho \"Tutti gli argomenti (come argomenti separati, v2 - \$@): $@\"\n\necho \"\nIterazione su tutti gli argomenti usando \$@:\n\"\nfor arg in \"$@\"\ndo\n  echo \"Argomento: $arg\"\ndone\n\necho \"\nScript terminato.\"\n
